import { Component } from '@angular/core';
import{Router} from '@angular/router'

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'ejemplo01';

  constructor(private router:Router){}

  Listar(){
    this.router.navigate(["listar"]);
  }

  Nuevo(){
    console.log('click sobre nuevo');
  }
}
<!-- Barra azul  -->
<div class="toolbar" role="banner">
  <img width="150" alt="Logo Cooperacion" src="/assets/img/cooperacion.png"/>
  <span class="titulo">Inventario IT</span>
</div>
<!--botones-->
<div class="container">
  <button (click)="Listar()" class="btn btn-info">Lista completa</button>
  <button (click)="Nuevo()"class="btn btn-info" style="margin-left:10px" >Nuevo elemento</button>
</div>

<router-outlet></router-outlet>
